import React, {useState} from 'react';
import PropTypes from 'prop-types';
import '../../assets/scss/components/Header/HeaderSearchOption.scss';
import {set} from 'date-fns';

HeaderSearchOption.propTypes = {};

function HeaderSearchOption(props) {
  const [optionOpen, setOptionOpen] = useState(false);
  const [option, setOption] = useState({
    adult: 1,
    children: 0,
    room: 1,
  });

  const handleOptionOpen = () => {
    setOptionOpen((optionOpen) => !optionOpen);
  };

  const handleCounterChange = (name, operator) => {
    setOption((preState) => {
      return {
        ...preState,
        [name]: operator === 'i' ? option[name] + 1 : option[name] - 1,
      };
    });
  };
  return (
    <div className='option'>
      <span className='headerSearch__text' onClick={handleOptionOpen}>
        {`${option.adult}: adult ${option.children}: children ${option.room}: room`}
      </span>

      {optionOpen && (
        <div className='option__container'>
          <div className='option__item'>
            <span className='option__text'>Adult</span>
            <div className='option__btn'>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('adult', 'd')}
              >
                -
              </button>
              <span className='option__number'>1</span>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('adult', 'i')}
              >
                +
              </button>
            </div>
          </div>

          <div className='option__item'>
            <span className='option__text'>Children</span>
            <div className='option__btn'>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('children', 'd')}
              >
                -
              </button>
              <span className='option__number'>0</span>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('children', 'i')}
              >
                +
              </button>
            </div>
          </div>

          <div className='option__item'>
            <span className='option__text'>Room</span>
            <div className='option__btn'>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('room', 'd')}
              >
                -
              </button>
              <span className='option__number'>1</span>
              <button
                className='option__countersBtn'
                onClick={() => handleOptionOpen('room', 'i')}
              >
                +
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default HeaderSearchOption;
