import React from 'react';
import PropTypes from 'prop-types';
import {format} from 'date-fns';

import '../../assets/scss/components/SearchResult/SearchBody.scss';
import {Container} from 'react-bootstrap';
import {DateRange} from 'react-date-range';
import {setDate} from 'date-fns/esm';

SearchBody.propTypes = {
  searchFilter: PropTypes.object,
};

SearchBody.defaultProps = {
  searchFilter: {},
};

function SearchBody(props) {
  const {searchFilter} = props;
  const {inputValue, range, option} = searchFilter;
  return (
    <Container>
      <div className='search'>
        <div className='search__popup'>
          <h1 className='search__title'>Search</h1>

          <div className='search__item'>
            <label className='search__label'>Destination</label>
            <input
              className='search__input'
              type='text'
              placeholder={inputValue}
            />
          </div>

          <div className='search__item'>
            <label className='search__label'>Check in date</label>
            <span className='search__text'>
              {`${format(range[0].startDate, 'MM/dd/yyyy')} to ${format(
                range[0].endDate,
                'MM/dd/yyyy'
              )}`}
            </span>
          </div>

          <div className='search__item'>
            <label className='search__label'>Option</label>

            <div className='option'>
              <span className='option__label'>
                Min price <small>per night</small>
              </span>
              <input type='text' className='option__input' />
            </div>

            <div className='option'>
              <span className='option__label'>
                Max price <small>per night</small>
              </span>
              <input type='text' className='option__input' />
            </div>

            <div className='option'>
              <span className='option__label'>Adult</span>
              <input
                type='text'
                className='option__input'
                placeholder={option.adult}
              />
            </div>

            <div className='option'>
              <span className='option__label'>Children</span>
              <input
                type='text'
                className='option__input'
                placeholder={option.children}
              />
            </div>

            <div className='option'>
              <span className='option__label'>Room</span>
              <input
                type='text'
                className='option__input'
                placeholder={option.room}
              />
            </div>
          </div>
        </div>
        <div className='search__list'></div>
      </div>
    </Container>
  );
}

export default SearchBody;
